import { createStore } from "vuex";
export default createStore({
  state: {
    todos: [
      {
        title: "todo item completed A",
        completed: false,
      },
      {
        title: "todo item completed B",
        completed: false,
      },
    ],
  },
  getters: {},
  mutations: {
    NEW_TODO(state,todoItem){
      state.todos.push({
        title:todoItem
      })
    }
  },
  actions: {
    addNewTodoItem({ commit }, todoItem) {
      commit("NEW_TODO", todoItem);
    },
  },
});
