import shop from "./shop";
export default createStore({
  state: {
    //like data
    products: [],
  },
  getters: {
    //like computed properties
    productsCount() {},
    availableProducts(state) {
      return state.products.filter((product) => product.inventory > 0);
    },
  },
  actions: {
    //make the ajax call
