import Vuex from "vuex";
import Vue from "vue";
Vue.use(Vuex);
export default new Vuex.Store({
  state: {
    products: [],
  },
  getters: {
    productsCount() {},
  },
  actions: {
    //make the ajax call
    //run setProducts mutation
    fetchProducts() {},
  },
  mutations: {
    //setting and updating state
    //when state change
    setProducts(state, products) {
      state.products = products;
    },
  },
});
