import { createStore } from "vuex";
export default createStore({
  state: {
    products: [],
  },
  getters: {
    productsCount() {},
  },
  actions: {
    //make the ajax call
    //run setProducts mutation
    fetchProducts() {},
  },
  mutations: {
    //setting and updating state
    //when state change
    setProducts(state, products) {
      state.products = products;
    },
  },
});
